"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[11979],{73939:e=>{e.exports=JSON.parse('{"functions":[],"properties":[],"types":[],"name":"SoftShutdownService","desc":"This service lets you shut down servers without losing a bunch of players.\\nWhen game.OnClose is called, the script teleports everyone in the server\\ninto a reserved server.\\n\\nWhen the reserved servers start up, they wait a few seconds, and then\\nsend everyone back into the main place.\\n\\nOriginally written by Merely\\nhttps://github.com/MerelyRBLX/ROBLOX-Lua/blob/master/SoftShutdown.lua\\n\\nModified by Quenty","source":{"line":16,"path":"src/softshutdown/src/Server/SoftShutdownService.lua"}}')}}]);